import * as React from "react";
import { motion, Variants } from "framer-motion";
import "./LoginSignup.css";
import img2 from "../RightTopimg.svg";

const vw = Math.max(document.documentElement.clientWidth, window.innerWidth || 0);
const vh = Math.max(document.documentElement.clientHeight, window.innerHeight || 0);

const signupPage = {
    signup: {
        x: -0.9 * vw,
        y: 0.9 * vh
    },
    login: {
        x: 0,
        y: 0
    }
};

class Login extends React.Component {

    constructor() {
        super();
        this.state = {
            username: '',
            password: '',
            email: '',
            signup: false,
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.setState = this.setState.bind(this);
    }

    handleChange(event){
        this.setState({[event.target.id]: event.target.value})
    }

    handleSubmit(event) {
        fetch('/login', {
              method: 'POST',
              headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                username: this.state.username,
                password: this.state.password
            })
        }).then(response => {
            if (response.ok) {
                localStorage.setItem('auth', true);
                return response.json();
            } else {
                throw new Error('Sum ting wong wi tu lo ho lee fuk bang ding ow...');
            }
        })
        window.location = "http://localhost:5000";
        event.preventDefault();
    }

    render() {
        return(
            <div class = "loginpage">
                <img className="img2" src={img2} width="400" height="400"/>
                <div className="titlel1">
                    <h1 className="titlel2">Slate</h1>
                    <h1 className = "titlel3">Welcome Back</h1>
                </div>
            </div>
        )
    }
}

export default Login;
