import React from 'react';
import ReactDOM from 'react-dom';

class AddScheduleBlock extends React.Component {

    constructor(){
        super();
        this.state = {name: "", start: "", end: "", day: ""};
        this.StoreValues = this.StoreValues.bind(this);
    }

    changeName(name){
        this.state.name = name;
    }

    changeStart(start){
        this.state.start = start;
    }

    changeEnd(end){
        this.state.end = end;
    }

    changeDay(day){
        this.state.day = day;
    }

    StoreValues(e){
        this.setState({name: e.target.name});
    }

    Submit = e => {
        e.preventDefault();
        this.props.action(this.state.name, this.state.start, this.state.end, this.state.day);
    }

    render(){
        return(
            <div>
                <form >
                    Event Name: <input type = "text" value = {this.state.name} onChange = {this.StoreValues}/>
                    Event Start: <input type = "text" value = {this.state.start} onChange = {this.StoreValues}/>
                    Event End: <input type = "text" value = {this.state.end} onChange = {this.StoreValues}/>
                    Day: <input type = "text" value = {this.state.day} onChange = {this.StoreValues}/>
                    <input type = "submit" value = "Submit"/>
                </form>
                <h1>
                    {this.state.name}
                </h1>
            </div>
        );
    }
}

export default AddScheduleBlock;
