import React from 'react';
import ReactDOM from 'react-dom';

class AddScheduleBlock extends React.Component {

    constructor(){
        super();
        this.state = {name: "", start: "", end: "", day: ""};
    }

    changeName(name){
        this.state.name = name;
    }

    changeStart(start){
        this.state.start = start;
    }

    changeEnd(end){
        this.state.end = end;
    }

    changeDay(day){
        this.state.day = day;
    }

    StoreValues = e =>{
        this.setState((state) => {
           return {name: this.namer};
        });
        this.props.action(this.state.name, this.state.start, this.state.end, this.state.day);
    }

    Submit = e => {

    }

    render(){
        return(
            <div>
                <form onSubmit={this.Submit}>
                    Event Name: <input type = "text" name = "Block Name" value = {this.state.name} onChange = {this.StoreValues}/>
                    Event Start: <input type = "text" name = "estart" value = {this.state.start}/>
                    Event End: <input type = "text" name = "eend" value = {this.state.end}/>
                    Day: <input type = "text" name = "day" value = {this.state.day}/>
                    <input type = "submit" value = "Submit"/>
                </form>
                <h1>
                    {this.state.name}
                </h1>
            </div>
        );
    }
}

export default AddScheduleBlock;
