import React from 'react';
import './PPc.css';
import "./SettingsStyles.css";
import img2 from "./RightTopimg.svg";
import history from "./history.js";
import {NavLink} from "react-router-dom";

class ProfilePage extends React.Component{
     constructor(props){
         super(props);
         this.state = {
             active: false
         };
         this.togglePopup = this.togglePopup.bind(this);
     }

     togglePopup() {
         this.setState({
             active: !this.state.active
         });
     }

     handleSubmit() {
         fetch('/logout', {
             method: 'POST'
         }).then(response => {
             if (response.ok) {
                 localStorage.setItem('auth', 'false');
                 history.push('/');
             } else {
                 history.push('/pp');
             }
         })
     }
     render(){
         return(
             <div>
                 <div className = "container">
                     <div className = "container2">
                         <img className="img2p" src={img2} width="400" height="400"/>
                         <NavLink to = "/pp"><h1 className="titlepp">Slate</h1></NavLink>
                         <div className = "main2">
                             <div class = "profile">
                                 <h1>Profile</h1>
                                 <div className="settingsBox">
                                     <button className="settingButton" onClick = {this.togglePopup}>Change Password</button>
                                     <button className="settingButton">Change Email</button>
                                     <button className="settingButton">Change Schedule</button>
                                     <button className="settingButton" onClick={this.handleSubmit}>Log Out</button>
                                 </div>
                                 <form className = "ppform">
                                     <div className = "search">
                                         <h6 className = "searchfrt">Search for Friends</h6>
                                         <input className = "searchBar" type = "text" placeholder = "Search..."/>
                                     </div>
                                 </form>
                             </div>
                             <div class = "feed">
                                 <h1>Feed</h1>
                             </div>
                         </div>
                     </div>
                 </div>
                 {this.state.active ? <Popup toggle = {this.togglePopup}></Popup>: null}
             </div>
         );
     }
}

class Popup extends React.Component {

    constructor(props){
        super(props);
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        this.props.toggle();
    }

    render() {
        return (
            <div className = "modal">
                <div className = "content">
                    <span className = "close" onClick = {this.handleClick}>&times;</span>
                    <p >Text</p>
                </div>
            </div>
        );
    }
}

export default ProfilePage;
