import React from 'react';
import ReactDOM from 'react-dom';

class ScheduleBlock {
    constructor(name, duration, start, end, day) {
        this.state = {name: name, duration: duration, start: start, end: end, day: day};
    }
}

class Schedule extends React.Component {
    blocks = [];

    constructor(){
        super();
        this.state = {timetable: []};
        for (var i = 0;i<144;i++){
            this.state.timetable[i] = [];
            for (var c = 0;c<7;c++){
                this.state.timetable[i][c] = "0";
            }
        }
    }

    addScheduleBlock(block){
        //Retrieves schedule blocks from Database and then adds new block
        this.blocks.add(block);
    }

    addTimeTable(block){
        for (var i = 0;i<block.length;i++){
            var name = blocks[i].name;
            var duration = blocks[i].duration;
            var start = blocks[i].start;
            var end = blocks[i].end;
            var day = blocks[i].day;
            var temp = start.split(":");
            var time = Math.floor((Integer.parseInt(temp[0])*60+Integer.parseInt(temp[1]))/10);
            this.state.timetable[time][day] = name + " - [" + start + "-" + end;
            for (var i = 1;i<=duration/10;i++){
                this.state.timetable[i][day] = 1;
            }
        }
    }

    render(){
        var schedule = [], cols = 145;
        var days = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
        for (var i = 0;i<145;i++){
            schedule[i] = [];
        }
        schedule[0][0] = (<td>
                            <h3>Time</h3>
                          </td>);
        for (var i = 1;i<8;i++) {
            schedule[0][i] = (<td>
                                <h3>{days[i - 1]}</h3>
                              </td>);
        }
        for (var i = 1;i<145;i++){
            if ((i-1)%6==0) {
                schedule[i][0] = (<td>
                                    <h3>{(Math.floor(i/6)).toString() + ":00"}</h3>
                                  </td>);
            }
            else {
                schedule[i][0] = (<td>
                                    <h3></h3>
                                  </td>);
            }
        }
        for (var i = 1;i<145;i++){
            for (var c = 1;c<8;c++){
                schedule[i][c] = (<td>
                                    <h3>{this.state.timetable[i-1][c-1]}</h3>
                                  </td>);
            }
        }
        var format = [145];
        for (var i = 0;i<145;i++){
            format[i] = (<tr>
                            {schedule[i]}
                         </tr>);
        }
        return(
            <div>
                <table>
                    {format}
                </table>
            </div>
        );
    }
}

Schedule.addScheduleBlock(new ScheduleBlock("SE101", 60, "1:00","2:00", 1));

export default Schedule;
